import React, { useState, useEffect } from 'react';
import { Container, Alert } from 'react-bootstrap';
import api from './api';
import Login from './components/Login';
import Register from './components/Register';
import EmployeeList from './components/EmployeeList';
import EmployeeFormModal from './components/EmployeeFormModal';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('token'));
  const [showRegister, setShowRegister] = useState(false);
  const [employees, setEmployees] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentEmployee, setCurrentEmployee] = useState(null);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const fetchEmployees = async () => {
    try {
      const { data } = await api.get('/employees');
      setEmployees(data);
      setError(null);
    } catch (err) {
      setError(err.response?.data?.error?.message || 'Failed to fetch employees');
    }
  };

  useEffect(() => {
    if (isLoggedIn) {
      fetchEmployees();
    }
  }, [isLoggedIn]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setEmployees([]);
  };

  const handleEdit = (employee) => {
    setCurrentEmployee(employee);
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      try {
        await api.delete(`/employees/${id}`);
        setSuccess('Employee deleted successfully');
        fetchEmployees();
      } catch (err) {
        setError(err.response?.data?.error?.message || 'Delete failed');
      }
    }
  };

  const handleSaveEmployee = async (employeeData) => {
    try {
      if (currentEmployee) {
        await api.put(`/employees/${currentEmployee._id}`, employeeData);
        setSuccess('Employee updated successfully');
      } else {
        await api.post('/employees', employeeData);
        setSuccess('Employee created successfully');
      }
      setShowModal(false);
      setCurrentEmployee(null);
      fetchEmployees();
      setError(null);
    } catch (err) {
      setError(err.response?.data?.error?.message || 'Operation failed');
    }
  };

  if (!isLoggedIn) {
    return showRegister ? (
      <Register switchToLogin={() => setShowRegister(false)} />
    ) : (
      <Login onLogin={() => setIsLoggedIn(true)} switchToRegister={() => setShowRegister(true)} />
    );
  }

  return (
    <Container className="mt-5">
      {error && <Alert variant="danger" onClose={() => setError(null)} dismissible>{error}</Alert>}
      {success && <Alert variant="success" onClose={() => setSuccess(null)} dismissible>{success}</Alert>}

      <EmployeeList
        employees={employees}
        onEdit={handleEdit}
        onDelete={handleDelete}
        onAdd={() => setShowModal(true)}
        onLogout={handleLogout}
      />

      <EmployeeFormModal
        show={showModal}
        onHide={() => {
          setShowModal(false);
          setCurrentEmployee(null);
        }}
        onSubmit={handleSaveEmployee}
        employee={currentEmployee}
      />
    </Container>
  );
}

export default App;
